import 'package:{FLUTTER_ASSIST}/core/cache/app_cache.dart';
import 'package:{FLUTTER_ASSIST}/core/cache/hive_cache_initialize.dart';
import 'package:{FLUTTER_ASSIST}/core/deep_link/deep_link_initialize.dart';
import 'package:{FLUTTER_ASSIST}/core/dependency/app_dependency.dart';
import 'package:{FLUTTER_ASSIST}/firebase_options.dart';
import 'package:easy_localization/easy_localization.dart';
import 'package:firebase_core/firebase_core.dart';
import 'package:firebase_crashlytics/firebase_crashlytics.dart';
import 'package:flutter/foundation.dart';
import 'package:flutter/material.dart';
import 'package:flutter/services.dart';

final class AppInitialize {
  Future<void> init() async {
    WidgetsFlutterBinding.ensureInitialized();
    await Firebase.initializeApp(options: DefaultFirebaseOptions.currentPlatform);
    FlutterError.onError = FirebaseCrashlytics.instance.recordFlutterError;
    await FirebaseCrashlytics.instance.setCrashlyticsCollectionEnabled(!kDebugMode);
    await EasyLocalization.ensureInitialized();
    await SystemChrome.setPreferredOrientations([DeviceOrientation.portraitUp]);
    await AppCache(cacheInitialize: HiveCacheInitialize()).init();
    await const AppDependency().init();
    await DeepLinkInitialize().init();
  }
}
